package week_1;

public class Simple_control_stuctures {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int temp = 22;
		if (temp > 30)
		{
			System.out.println("Its Very Hot");
		}
		else {
			System.out.println("Its Cold");
		}
		
		System.out.println("\nI will count till 5");
		for (int i = 1; i < 6; i++)
		{
			System.out.print(+i);
		}

	}

}
