package week_1;

public class Simple_operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		int b = 2;
		
		int s = a+b;
		int d = a-b;
		
		System.out.println("Sum: " +s);
		System.out.println("Difference: " +d);
		boolean bool = a > b;
		System.out.println("Is A greater than B?: " +bool);
	}

}
